import React, { useEffect, useState } from "react";
import {
  ComponentProps,
  Streamlit,
  withStreamlitConnection,
} from "streamlit-component-lib";
import { Slider } from "baseui/slider";



const CustomSlider = (props: ComponentProps) => {

  const [api, setapi] = useState("f13e245aad384f8f97e7fe525511ec8c");


  function fetchNewsDetails(query: any) {
    const apiKey = api; // Replace with your actual API key
    const baseUrl = "https://api.newsapi.com/v2"; // Replace with the base URL of the news API

    // Construct the request URL with the query parameter
    const url = `${baseUrl}/articles?q=${query}&apiKey=${apiKey}`;

    // Send the HTTP GET request using fetch()
    fetch(url)
      .then(response => {
        // Check if the response was successful (status code 200)
        if (response.ok) {
          // Parse the response as JSON
          return response.json();
        } else {
          // Handle the case when the response is not successful
          throw new Error(`Error: ${response.status}`);
        }
      })
      .then(data => {
        // Process the data as needed
        // For example, log the titles of the news articles
        for (const article of data.articles) {
          console.log(article.title);
        }
      })
      .catch(error => {
        // Handle any errors that occurred during the request
        console.error("Error:", error);
      });
  }



  /**
   * Destructuring JSON objects is a good habit.
   * This will look for label, minValue and maxValue keys
   * to store them in separate variables.
   */
  const { label, minValue, maxValue, label2 } = props.args;

  const [value, setValue] = useState([10]);

  useEffect(() => Streamlit.setFrameHeight());

  // Add a label and pass min/max variables to the baseui Slider
  return (
    <>
      <h1>Name - {label}</h1>
      <h2>{maxValue}</h2>

      <h3>Country - {label2}</h3>
    </>
  );
};

export default withStreamlitConnection(CustomSlider);
